 * 1. repetitive html
 * 2. big pages
 * 3. things that change often
 * @returns 
 */
function Modal(props) {
  return (
    <div className="modal">
      <h4>{ props.title[props.current] }</h4>
      <p>Date</p>
      <p>Detail</p>
      <button>changeTitle</button>
    </div>
  )
}

// use {} for data binding

export default App;

