 * When is component good?
 * 1. repetitive html
 * 2. big pages
 * 3. things that change often
 * @returns 
 */
function Modal() {
  return (
    <div className="modal">
      <h4>Title</h4>
      <p>Date</p>
      <p>Detail</p>
    </div>
  )
}

